    public static function getAbilityArray($abilityString, \DND\Objects\Character &$obj = NULL) {
        $d = explode(',', $abilityString);
        $rslt = [
            'strength' => 0,
            'dexterity' => 0,
            'constitution' => 0,
            'intelligence' => 0,
            'wisdom' => 0,
            'charisma' => 0,
            #--
            'rangeAtk' => 0,
            'rangeDmg' => 0,
            'meleeAtk' => 0,
            'meleeDmg' => 0,
            'spellAtk' => 0,
            'spellDc' => 0,
            'atk' => 0,
            'dmg' => 0,
            'ac' => 0,
            'savingThrows' => 0,
            'proficiency' => 0,
            'passivWisdom' => 0,
            'spellDc' => 0,
            'spellAttack' => 0
        ];
        foreach ($d as $zeichenkette) {
            $suchmuster = '/(\+|\-)(\d+)(.+)/';
            preg_match($suchmuster, $zeichenkette, $treffer);
            switch ($treffer[3]) {
                case 'str':
                    if ($treffer[1] == '+')
                        $rslt['strength'] += $treffer[2];
                    if ($treffer[1] == '-')
                        $rslt['strength'] -= $treffer[2];
                    if ($treffer[1] == '=' && $obj != NULL) {
                        $obj->setStrength($treffer[2]);
                    }
                    break;
                case 'dex':
                    if ($treffer[1] == '+')
                        $rslt['dexterity'] += $treffer[2];
                    if ($treffer[1] == '-')
                        $rslt['dexterity'] -= $treffer[2];
                    if ($treffer[1] == '=' && $obj != NULL) {
                        $obj->setDexterity($treffer[2]);
                    }
                    break;
                case 'con':
                    if ($treffer[1] == '+')
                        $rslt['constitution'] += $treffer[2];
                    if ($treffer[1] == '-')
                        $rslt['constitution'] -= $treffer[2];
                    if ($treffer[1] == '=' && $obj != NULL) {
                        $obj->setConstitution($treffer[2]);
                    }
                    break;
                case 'int':
                    if ($treffer[1] == '+')
                        $rslt['intelligence'] += $treffer[2];
                    if ($treffer[1] == '-')
                        $rslt['intelligence'] -= $treffer[2];
                    if ($treffer[1] == '=' && $obj != NULL) {
                        $obj->setIntelligence($treffer[2]);
                    }
                    break;
                case 'wis':
                    if ($treffer[1] == '+')
                        $rslt['wisdom'] += $treffer[2];
                    if ($treffer[1] == '-')
                        $rslt['wisdom'] -= $treffer[2];
                    if ($treffer[1] == '=' && $obj != NULL) {
                        $obj->setWisdom($treffer[2]);
                    }
                    break;
                case 'cha':
                    if ($treffer[1] == '+')
                        $rslt['charisma'] += $treffer[2];
                    if ($treffer[1] == '-')
                        $rslt['charisma'] -= $treffer[2];
                    if ($treffer[1] == '=' && $obj != NULL) {
                        $obj->setCharisma($treffer[2]);
                    }
                    break;
            }
        }

        return $rslt;
    }

    public static function getModifier($value) {
        if ($value >= 30)
            return 10;
        if ($value >= 28)
            return 9;
        if ($value >= 26)
            return 8;
        if ($value >= 24)
            return 7;
        if ($value >= 22)
            return 6;
        if ($value >= 20)
            return 5;
        if ($value >= 18)
            return 4;
        if ($value >= 16)
            return 3;
        if ($value >= 14)
            return 2;
        if ($value >= 12)
            return 1;
        if ($value >= 10)
            return 0;
        if ($value >= 8)
            return -1;
        if ($value >= 6)
            return -2;
        if ($value >= 4)
            return -3;
        if ($value >= 2)
            return -4;
        return -5;
    }

