    public static function getProficiency($level) {
        switch ($level) {
            case 0:
            case 1:
            case 2:
            case 3:
            case 4:
                return 2;
                break;
            case 5:
            case 6:
            case 7:
            case 8:
                return 3;
                break;
            case 9:
            case 10:
            case 11:
            case 12:
                return 4;
                break;
            case 13:
            case 14:
            case 15:
            case 16:
                return 5;
                break;
            default:
                return 6;
                break;
        }
    }

